u.user_email = f.target_email
f.user_email = #{userEmail}
f.user_email = #{userEmail}
and u.nick = #{searchKeyword}
ORDER BY f.friend_black_no ) inner_table WHERE ROWNUM <= #{endRowNum} ) WHERE row_seq BETWEEN #{startRowNum} AND #{endRowNum}

