mapper.select:
SELECT * FROM ( SELECT inner_table.* , ROWNUM AS row_seq FROM ( select u.user_email, u.nick, u.profile_img from users u, friend_blacks f WHERE u.user_email = f.target_email and f.f_b_status = '1' and f.acc_status = '2' and f.user_email = #{userEmail} union select u.user_email, u.nick, u.profile_img from users u, friend_blacks f where u.user_email = f.user_email and f.f_b_status = '1' and f.acc_status = '2' and f.target_email = #{userEmail}
AND u.nick = #{searchKeyword}
) inner_table WHERE ROWNUM <= #{endRowNum} ) WHERE row_seq BETWEEN #{startRowNum} AND #{endRowNum}

SELECT * FROM ( SELECT inner_table.* , ROWNUM AS row_seq FROM ( SELECT u.user_email, u.nick, u.profile_img FROM users u, friend_blacks f
u.user_email = f.target_email AND f.f_b_status = '2' AND f.user_email = #{userEmail}
AND u.nick = #{searchKeyword}
ORDER BY f.friend_black_no ) inner_table WHERE ROWNUM <= #{endRowNum} ) WHERE row_seq BETWEEN #{startRowNum} AND #{endRowNum}

SELECT * FROM ( SELECT inner_table.* , ROWNUM AS row_seq FROM ( SELECT u.user_email, u.nick, u.profile_img, f.acc_status FROM users u, friend_blacks f
u.user_email = f.user_email and f.f_b_status = '1' and f.acc_status = '1' and f.target_email = #{targetEmail}
ORDER BY f.friend_black_no ) inner_table WHERE ROWNUM <= #{endRowNum} ) WHERE row_seq BETWEEN #{startRowNum} AND #{endRowNum}

SELECT COUNT(*) FROM( select u.nick, u.profile_img from users u, friend_blacks f where u.user_email = f.target_email and f.f_b_status = '1' and f.acc_status = '2')
u.nick = #{searchKeyword}
) countTable

mapper.update:
UPDATE friend_blacks
acc_status = '2'
WHERE f_b_status = '1' and user_email = #{userEmail}

mapper.delete:
delete from friend_blacks 
WHERE f_b_status = '1' and acc_status = '1'  
and user_email = #{userEmail}
