mapper.update:
UPDATE badges
badge_name = #{badgeName:VARCHAR} , badge_detail = #{badgeDetail:VARCHAR} , act_count = #{actCount} , badge_img = #{badgeImg:VARCHAR} , grade = #{grade:VARCHAR} , status = #{status:VARCHAR}
WHERE badge_no = #{badgeNo}

mapper.select:
SELECT * FROM ( SELECT inner_table.* , ROWNUM AS row_seq FROM ( SELECT badge_no, badge_name , badge_img, act_count, grade , badge_detail, status FROM badges
status='0'
ORDER BY badge_no ) inner_table WHERE ROWNUM <= #{endRowNum} ) WHERE row_seq BETWEEN #{startRowNum} AND #{endRowNum}

SELECT * FROM ( SELECT inner_table.* , ROWNUM AS row_seq FROM ( SELECT badge_no, badge_name , badge_img, act_count, grade , badge_detail, status FROM badges
status='1'
ORDER BY badge_no ) inner_table WHERE ROWNUM <= #{endRowNum} ) WHERE row_seq BETWEEN #{startRowNum} AND #{endRowNum}
