mapper.select:
SELECT * FROM ( SELECT inner_table.* , ROWNUM AS row_seq FROM ( SELECT a.my_badge_no, a.user_email, a.badge_no, a.act_count, b.badge_name, b.badge_img, b.badge_detail, b.status FROM my_badge a INNER JOIN badges b ON a.badge_no = b.badge_no WHERE a.user_email = #{ userEmail:VARCHAR }
and status = '0'
ORDER BY a.my_badge_no ) inner_table WHERE ROWNUM <= #{endRowNum} ) WHERE row_seq BETWEEN #{startRowNum} AND #{endRowNum}

SELECT * FROM ( SELECT inner_table.* , ROWNUM AS row_seq FROM ( SELECT a.my_badge_no, a.user_email, a.badge_no, a.act_count, b.badge_name, b.badge_img, b.badge_detail, b.status FROM my_badge a INNER JOIN badges b ON a.badge_no = b.badge_no WHERE a.user_email = #{ userEmail:VARCHAR }
and status = '1'
ORDER BY a.my_badge_no ) inner_table WHERE ROWNUM <= #{endRowNum} ) WHERE row_seq BETWEEN #{startRowNum} AND #{endRowNum}

SELECT COUNT(*) FROM( SELECT my_badge_no, badge_no, user_email , act_count FROM my_badge
user_email = #{userEmail}
my_badge_no LIKE '%${searchKeyword}%'
badge_no LIKE '%${searchKeyword}%'
) countTable
