mapper.update:
UPDATE notice_faqs
status = #{status} , notice_must = #{noticeMust} , title = #{title} , detail = #{detail} , img = #{img} , faq_tag = #{faqTag}
WHERE notice_faq_no = #{noticeFaqNo}

mapper.select:
  	SELECT *
  	FROM (	SELECT inner_table.* , ROWNUM AS row_seq
  					FROM		(	SELECT notice_faq_no , status, notice_must, title , detail,  reg_date, faq_tag
										FROM notice_faqs



									 				notice_faq_no = #{searchKeyword}


									 				title = #{searchKeyword}



										ORDER BY notice_faq_no ) inner_table
					WHERE ROWNUM <= #{endRowNum} )
	WHERE row_seq BETWEEN #{startRowNum} AND #{endRowNum}

  	SELECT COUNT(*)
  	FROM(	SELECT notice_faq_no , title , detail
					FROM notice_faqs



					 			notice_faq_no = #{searchKeyword}


					 			title = #{searchKeyword}


					 ) countTable
